example.code:
int x_fp = (int) (9.2f * ONE);  
int rx_fp = round(x_fp);  // Sets rx_fp to 9.0
int rx = fptoi(rx_fp);    // Sets rx to 9

int y_fp = (int) (9.5f * ONE);
int ry_fp = round(y_fp);  // Sets ry_fp to 10.0
int ry = fptoi(ry_fp);    // Sets ry to 10

int z_fp = (int) (9.9f * ONE);
int rz_fp = round(z_fp);  // Sets rz_fp to 10.0
int rz = fptoi(rz_fp);    // Sets rz to 10
