example.code:
import processing.video.*;
Capture myCapture;

void setup() 
{
  size(200, 200);
  print(Capture.list());
  String s = "Logitech QuickCam Zoom-WDM";
  myCapture = new Capture(this, s, width, height, 30);
  myCapture.crop(10, 10, 180, 180);
}

void draw() {
  if (myCapture.available()) {
    myCapture.read();
  }
  image(myCapture, 10, 10);
}

void mousePressed() {
  myCapture.noCrop();
}
