example.code:
PFont font;
// The font must be located in the current sketch's
// "data" directory to load successfully
font = loadFont("LetterGothicStd-32.vlw");
textFont(font, 20);
textAlign(RIGHT);
text("word", 50, 30);
textAlign(CENTER);
text("word", 50, 50);
textAlign(LEFT);
text("word", 50, 70);
