example.code:
float a = PI/3;
float s = sin(a);
float as = asin(s);
// Prints "1.0471976 : 0.86602545 : 1.0471976"
println(a + " : " + s + " : " +  as);

float a = PI + PI/3.0;
float s = sin(a);
float as = asin(s);
// Prints "4.1887903 : -0.86602545 : -1.0471976"
println(a + " : " + s + " : " +  as);
